mapper.select:
select username, userid, email
from member
where userid = #{userid} and userpwd = #{userpwd}

select username, userid, email, joindate
from member
where userid = #{userid}

select token
from member
where userid = #{userid}

mapper.update:
update member
set token = #{token}
where userid = #{userid}

update member
set token = #{token, jdbcType=VARCHAR}
where userid = #{userid}

mapper.select:
select count(*)
from member
where userid = #{userid}

mapper.update:
update member
set userpwd = #{userpwd}
where userid = #{userid}

update member
set username = #{username} , email = #{email}
where userid = #{userid}

update member
set userpwd = #{newpassword}
where userpwd = #{originpassword} and userid = #{userid}
